mapper.insert:
insert into t_produce_component_inspection_approve (id, component_name, codes, 
  number, operator, status, 
  image, remark, approve_status, 
  approve_result, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{componentName,jdbcType=VARCHAR}, #{codes,jdbcType=VARCHAR}, 
  #{number,jdbcType=INTEGER}, #{operator,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{image,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{approveStatus,jdbcType=INTEGER}, 
  #{approveResult,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select component_name
from 
t_produce_component_inspection_approve


		and create_time >= #{startTime}


		and create_time <= #{stopTime}


GROUP BY component_name

select
id, component_name as name,  number, operator,  remark, image,
approve_status as process_instance_type,
approve_result as process_instance_result,
create_time as time
from t_produce_component_inspection_approve


        and component_name=#{type}


        and operator =#{operator}


        and create_time >= #{startTime}


        and create_time <= #{stopTime}

    order by create_time desc
